package com.sec.android.app.fm;import com.sec.android.app.fm.util.SystemPropertiesWrapper;public class Log {    private static final String ALTERNATIVE_STRING = "##";    public static final boolean DEBUG = (SystemPropertiesWrapper.getInstance().getInt("ro.debuggable", 0) == 1);    public static void d(String tag, String msg) {        android.util.Log.d(tag, msg);    }    public static void d(String tag, String msg, Throwable tr) {        android.util.Log.d(tag, msg, tr);    }    public static void e(String tag, String msg) {        android.util.Log.e(tag, msg);    }    public static void e(String tag, String msg, Throwable tr) {        android.util.Log.e(tag, msg, tr);    }    public static String filter(float val) {        return DEBUG ? String.valueOf(val) : ALTERNATIVE_STRING;    }    public static String filter(int val) {        return DEBUG ? String.valueOf(val) : ALTERNATIVE_STRING;    }    public static String filter(long val) {        return DEBUG ? String.valueOf(val) : ALTERNATIVE_STRING;    }    public static String filter(Object val) {        return DEBUG ? String.valueOf(val) : ALTERNATIVE_STRING;    }    public static String filter(String str) {        return DEBUG ? str : ALTERNATIVE_STRING;    }    public static void i(String tag, String msg) {        android.util.Log.i(tag, msg);    }    public static void i(String tag, String msg, Throwable tr) {        android.util.Log.i(tag, msg, tr);    }    public static void secD(String tag, String msg) {        android.util.secutil.Log.secD(tag, msg);    }    public static void secD(String tag, String msg, Throwable tr) {        android.util.secutil.Log.secD(tag, msg, tr);    }    public static void secE(String tag, String msg) {        android.util.secutil.Log.secE(tag, msg);    }    public static void secE(String tag, String msg, Throwable tr) {        android.util.secutil.Log.secE(tag, msg, tr);    }    public static void secI(String tag, String msg) {        android.util.secutil.Log.secI(tag, msg);    }    public static void secI(String tag, String msg, Throwable tr) {        android.util.secutil.Log.secI(tag, msg, tr);    }    public static void secV(String tag, String msg) {        android.util.secutil.Log.secV(tag, msg);    }    public static void secV(String tag, String msg, Throwable tr) {        android.util.secutil.Log.secV(tag, msg, tr);    }    public static void secW(String tag, String msg) {        android.util.secutil.Log.secW(tag, msg);    }    public static void secW(String tag, String msg, Throwable tr) {        android.util.secutil.Log.secW(tag, msg, tr);    }    public static void v(String tag, String msg) {        android.util.Log.v(tag, msg);    }    public static void v(String tag, String msg, Throwable tr) {        android.util.Log.v(tag, msg, tr);    }    public static void w(String tag, String msg) {        android.util.Log.w(tag, msg);    }    public static void w(String tag, String msg, Throwable tr) {        android.util.Log.w(tag, msg, tr);    }}