    public static boolean hasEmojiString(CharSequence chars) {
        Pattern ptn = getPattern();
        if (ptn != null) {
            Matcher match = ptn.matcher(chars);
            return match.find();
        } else {
            return false;
        }
    }

    public static String exceptEmojiString(CharSequence chars) {
        Pattern ptn = getPattern();
        if (ptn != null) {
            Matcher match = ptn.matcher(chars);
            return match.replaceAll("");
        } else {
            return chars.toString();
        }
    }

    public static Pattern getPattern() {
        Pattern EMOJI_PATTERN = null;
        boolean patternMade = false;
        if (!patternMade) {
            StringBuilder emojiPattern = new StringBuilder(EmojiList.getUnicodeList().size() * 2);

            emojiPattern.append('(');

            for (Iterator<String> itr = EmojiList.getUnicodeList().iterator(); itr.hasNext();) {
                String str = itr.next();
                emojiPattern.append(Pattern.quote(unicodeToUTF16(str)));
                emojiPattern.append('|');
            }
            emojiPattern.replace(emojiPattern.length() - 1, emojiPattern.length(), ")");

            EMOJI_PATTERN = Pattern.compile(emojiPattern.toString());
            patternMade = true;
        }
        return EMOJI_PATTERN;
    }

    public static String unicodeToUTF16(String unicode) {
        String convertedUTF16Str = "";
        int unicodeInteger, zzzzz, yyyy;
        unicodeInteger = Integer.decode(unicode);

        if (unicode.length() - 2 > 4) {
            zzzzz = (0x1F0000 & unicodeInteger) >> 16;
            yyyy = zzzzz - 1;

            int rear2byte = 0x0;
            int rear2byteFixed = 0xDC00;
            rear2byte = rear2byteFixed | (unicodeInteger & 0x3FF);
            String rear2byteString = "0x" + Integer.toHexString(rear2byte);

            int former2byte = 0x0;
            int former2byteFixed = 0xD800;
            former2byte = former2byteFixed | ((unicodeInteger & 0xFC00) >> 10);
            former2byte = former2byte | (yyyy << 6);
            String former2byteString = "0x" + Integer.toHexString(former2byte);

            int rear2byteInt = Integer.decode(rear2byteString);
            int former2byteInt = Integer.decode(former2byteString);

            convertedUTF16Str = "" + (char) former2byteInt + (char) rear2byteInt;

        } else {
            convertedUTF16Str = "" + (char) unicodeInteger;
        }

        return convertedUTF16Str;
    }

